sys_script_include.script:
var SelfWateringUtils = Class.create();
SelfWateringUtils.prototype = {
    initialize: function() {
    },

    logMoistureReading: function(data) {
        // Example data: { device_id: "esp8266", moisture: 420 }
        var sys = new GlideRecord('x_461782_slf_water_system');
        sys.addQuery('device', data.device_id);
        sys.query();
        if (!sys.next()) {
            gs.error('No matching System for device: ' + data.device_id);
            return;
        }

        // Insert a new moisture log
        var log = new GlideRecord('x_461782_slf_water_log');
        log.initialize();
        log.system = sys.sys_id;
        log.moisture_level = data.moisture;
        log.source_device = data.device_id;
        log.status = 'received';
        log.insert();

        // If below threshold and auto_water is enabled
        if (sys.moisture_threshold && data.moisture < sys.moisture_threshold && sys.auto_water_enabled) {
            this.triggerWatering(sys);
        }
    },

    triggerWatering: function(systemGr) {
        systemGr.override_mode = true;
        systemGr.update();

        // Optionally insert another log
        var log = new GlideRecord('x_461782_slf_water_log');
        log.initialize();
        log.system = systemGr.sys_id;
        log.note = 'Auto-watering triggered';
        log.status = 'watered';
        log.watered = true;
        log.insert();
    },

    type: 'SelfWateringUtils'
};
