sys_script_include.script:
var WateringUtils = Class.create();
WateringUtils.prototype = {
    initialize: function () {},

    logWateringEvent: function (moistureLevel, sourceDevice, watered, note) {
        var log = new GlideRecord('x_461782_slf_water_log');
        log.initialize();
        log.moisture_level = moistureLevel;
        log.source_device = sourceDevice || 'ESP8266';
        log.watered = watered ? true : false;
        log.note = note || '';
        log.insert();
    },

    type: 'WateringUtils'
};
